import { LitElement, html, css } from 'lit-element';
import { greenValue } from 'state-green';
import { redValue } from 'state-red';

export class PageB extends LitElement {
  static get styles() {
    return css`
      :host {
        display: block;
        padding: 10px;
        border: 2px solid #ccc;
      }
    `;
  }

  render() {
    return html`
      <h3>I am page B</h3>
      <p>Green Value: ${greenValue}</p>
      <p>Red Value: ${redValue}</p>
    `;
  }
}

customElements.define('page-b', PageB);
